INSERT INTO `miembro_equipo` (`id_post`, `nombre`, `cargo`, `descripcion`, `id_categoria_equipo`) VALUES
(4, 'Michela Pentimalli', 'Directora del Espacio Patiño', 'Está a cargo de la dirección del Espacio Patiño en su conjunto y de su personal. Proyecta y supervisa la ejecución de las propuestas, las actividades, y los productos intelectuales (libros, catálogos, videos, CDs) de las distintas áreas del Espacio Patiño. Planifica y organiza, además, las investigaciones y exposiciones de artes visuales e histórico documentales que se presentan en el Espacio.   ', 1),
(6, 'María Tapia', 'Coordinadora de actividades culturales y de secretaría', 'Articula la logística de las actividades planeadas por Dirección y las distintas áreas que conforman el Espacio. Coordina las relaciones con artistas, profesionales, consultores y medios de comunicación. Se ocupa también de las tareas de organización secretarial.                  ', 1),
(38, 'Eloisa Vargas', 'Responsable del Centro de Información y Documentación', 'Está a cargo de la gestión de los Centros de información y documentación y de las adquisiciones bibliográficas. Organiza actividades culturales de extensión. Así mismo, realiza el servicio especializado de asesoramiento bibliográfico a investigadores y la selección y la supervisión de las pasantías. ', 2),
(68, 'Gonzalo Cansay', ' Administrador del Espacio Simón I. Patiño', 'Es responsable del cumplimiento de las políticas y normas establecidas por la institución en la gestión financiera y del personal, además del cumplimiento de las obligaciones con entidades fiscalizadoras. Supervisa y coordina las actividades de mantenimiento de los activos fijos de la institución. ', 7),
(87, 'Juan Conde', 'Técnico montajista y de mantenimiento', 'Es encargado de la logística de las exposiciones, del enmarcado de obras y tareas afines, de la conducción y mantenimiento de las movilidades; de mantenimiento de los equipos, del sistema de iluminación, de las instalaciones eléctricas.', 1),
(88, 'Natalio Choque', 'Auxiliar de la oficina de las Salas de Exposiciones', 'Realiza tareas de logística de las exposiciones. Cumple labores de mensajería y se encarga del mantenimiento de las oficinas de dirección y secretaría. Apoya en la atención al público en las actividades y eventos.', 1),
(89, 'Tania Soria', 'Técnico documentalista del CID', 'Está a cargo del tratamiento documental. También realiza servicios de referencia y circulación, capacitación de usuarios, supervisión de pasantes, venta de publicaciones, control del inventario, elaboración de informes y apoyo en actividades culturales.', 2),
(90, 'Álvaro Mollinedo', 'Técnico documentalista del C-Musical', 'Está acargo del tratamiento documental de la información. También realiza la digitalización, los servicios de referencia y circulación, capacitación de usuarios, supervisión de pasantes, elaboración de informes y apoyo en actividades culturales.', 2),
(91, 'Cirilo Vargas', 'Auxiliar de oficina del CID', 'Apoya en los servicios de referencia, circulación e inventario. Realiza el servicio de reprografía, tareas de mensajería y de logística en las actividades culturales; conduce vehículos de la institución.', 2),
(92, 'Sussy Soto', 'Responsable del CAP', 'Está a cargo de la planificación, ejecución y supervisión de las actividades de actualización educativa para profesores; animación a la lectura y desarrollo creativo para niños y niñas. Así mismo, coordina actividades vinculadas a la literatura infantil y organiza actividades lúdico pedagógicas y eventos afines.', 6),
(93, 'Angelina Araujo', 'asistente pedagógica del CAP', 'Colabora a la responsable del CAP en todas las actividades y eventos del área educativa. Está a cargo de algunos talleres de metodología de la lectura de comprensión y de animación a la lectura.', 6),
(94, 'Desiderio Aranda', 'Auxiliar de oficina del CAP', 'Es el encargado de la logística de las diferentes actividades del CAP, de su difusión y registro audiovisual; realiza también tareas de mensajería.', 6),
(95, 'Catti Fernández', 'Contadora', 'Está a cargo de la actualización de los sístemas contables, de personal y de activos fijos; del cumplimiento de las obligaciones sociales y tributarias, y tareas afines. Interactua con las auditorías internas y externas y apoya en tareas adiministrativas.', 7),
(96, 'Jhody Mendoza', 'Auxiliar de Administración', 'Es el responsable del adecuado control del los almacenes del material bibliográfico a la venta y de los insumos. Realiza trámites administrativos y apoya en la logística de las actividades de las distintas áreas.', 7);

